import 'package:string_validator/string_validator.dart';

class User {
  final String name;
  final String id;
  String img;
  bool isFirstYear;
  User({this.name, this.id, this.img}) {
    if (isBase64((img) ?? ""))
      this.isFirstYear = false;
    else {
      this.isFirstYear = true;
      this.img =
          "data:image/jpeg;base64,";
    }
  }
}
